sourcesContent:
const redis = require('redis');

class RedisClient {
    constructor() {
        // Create and connect the Redis client in the constructor
        this.client = redis.createClient({
            url: 'redis://localhost:6379' // Replace with your Redis URL
        });

        // Initialize the connection state
        this.isConnected = false;

        // Handle successful connection
        this.client.on('connect', () => {
            console.log('Connected to Redis!');
            this.isConnected = true; // Set connection status to true
        });

        // Handle connection errors
        this.client.on('error', (err) => {
            console.error('Redis connection error:', err);
            this.isConnected = false; // Set connection status to false on error
        });
    }

    // isAlive method to check the connection status
    isAlive() {
        return this.isConnected;
    }

    // Example method to set a value in Redis
    async setValue(key, value) {
        if (this.isAlive()) {
            await this.client.set(key, value);
        } else {
            console.error('Redis connection is not alive.');
        }
    }

    // Example method to get a value from Redis
    async getValue(key) {
        if (this.isAlive()) {
            const value = await this.client.get(key);
            return value;
        } else {
            console.error('Redis connection is not alive.');
        }
    }

    // Method to close the connection
    async closeConnection() {
        if (this.isAlive()) {
            await this.client.quit();
            console.log('Redis connection closed.');
        }
    }
}

module.exports = RedisClient

